//WRAPPER MENU
//------------------------------------
$('#close-wrapper-left').click(function(event) {
    event.preventDefault();
    $('.wrapper-left').removeClass('active');
});

$('#menu-toggle-left').on('click', function(e) {
    e.preventDefault();
    e.stopPropagation();

    $('.wrapper-left').toggleClass('active');
    $('.wrapper-left li a').on('click', function(){
        $('.wrapper-left').removeClass('active');
    });
    $(document).one('click', function closeMenu(e) {
        if ($('.wrapper-left').has(e.target).length === 0) {
            $('.wrapper-left').removeClass('active');
        } else {
            $(document).one('click', closeMenu);
        }
    });
});
