// ACCORDION
//---------------------------------------------------------------------------
(function(){

    var $currAccordion = null;
    
    $(".accordion").each(function(){
        var $accordion = $(this);

        $accordion.find(".question").click(function(e){
            e.preventDefault();
            var opened = $accordion.data("opened");

            if(!opened){
                $currAccordion && closeAccordion($currAccordion);
                $currAccordion = $accordion;
                showAccordion($accordion);
            }
            else{
                $currAccordion = null;
                closeAccordion($accordion);
            }
        });
    });

    function closeAccordion($accordion){
        $accordion.find(".question").removeClass('active icon-angle-up').addClass('icon-angle-down');
        $accordion.find(".answer").slideUp(300).removeClass("open");
        $accordion.data("opened", false);
    }

    function showAccordion($accordion){
        $accordion.find(".question").addClass('active icon-angle-up').removeClass('icon-angle-down');
        $accordion.find(".answer").slideDown(300).addClass("open");
        $accordion.data("opened", true);
    }

})();

// SUPER SPY
//--------------------------------------------------------
jQuery.easing.jspy = jQuery.easing.spy;
jQuery.extend(jQuery.easing, {
    scrollOut: function (e, f, a, h, g) {
        if (f == 0) {
            return a
        }
        if (f == g) {
            return a + h
        }
        if ((f /= g / 2) < 1) {
            return h / 2 * Math.pow(2, 10 * (f - 1)) + a
        }
        return h / 2 * (-Math.pow(2, -10 * --f) + 2) + a
    }
});
$(function () {
    $('a.page-scroll').bind('click', function (event) {
        var $anchor = $(this);
        $('html, body').stop().animate({
            scrollTop: $($anchor.attr('href')).offset().top
        }, 500, 'scrollOut');
        event.preventDefault();
    });
});
